summary <- modelsummary(models,
  exponentiate = TRUE,
  fmt = fmt_significant(3),
  estimate = "{estimate}",
  statistic = "{conf.low} - {conf.high}",
  coef_omit = "^(?!b_ich_lat)",
  coef_rename = c("b_ich_lateralityRight" = "Right Hemisphere ICH"),
  gof_map = c("none")
)

# gather odds ratios from modelsummary object
